import React, { Component, useState } from 'react';
import { StyleSheet, Text, View, Button, ImageBackground, TextInput } from 'react-native';
import ButtonRow from './ButtonRow'
// const App = () => {...}
export default function App() {
const [username,setUserName] = useState()
const [password, setPassword] = useState()
  return (
    <View style={styles.container}>
      <ImageBackground source={{uri: 'https://images.unsplash.com/photo-1481070555726-e2fe8357725c?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=870&q=80'}} style={styles.imageBackground}>
    </ImageBackground>
    <View style={styles.FormContainer}>
      <Text style={styles.inputext}>Login</Text>
        <TextInput
          value={username}
          onChangeText={(username) => setUserName({ username })}
          placeholder='Email'
          style={styles.input}
        />
        <TextInput
          value={password}
          onChangeText={(password) => setPassword({ password })}
          placeholder='Password'
          secureTextEntry={true}
          style={styles.input}
        />

        <Button
          title= "login"
          style={styles.input}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'stretch',
    justifyContent: 'center',
    alignItems:"center"
  },
  header: {
    flex:1,
    alignItems:'center',
    fontSize:24,
    padding:25,
    color:"while",
  },
  imageBackground: {
    width: '100%',
     height: '100%',
    opacity: 0.8
  },
  IngredientsInput:{
    height: 40,
    width:400,
    margin: 12,
    borderWidth: 1.5,
    padding: 10,
  },
  FormContainer: {
    flex: 1,
    width:300,
    heightL500,
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute', 
    backgroundColor:"white",
  },
  input: {
    width: 200,
    height: 44,
    padding: 10,
    borderWidth: 1,
    borderColor: 'black',
    marginBottom: 10,
  },
  inputext: {
    width: 200,
    height: 44,
    padding: 10,
    textAlign:'center',
    fontWeight:'bold',
    borderWidth: 1,
    borderColor: 'black',
    marginBottom: 10,
  },
});
