import React, { Component } from 'react';
import { StyleSheet, Text, View, Button, ImageBackground, TextInput } from 'react-native';
import ButtonRow from './ButtonRow'
// const App = () => {...}
export default function App() {
  return (
    <View style={styles.container}>
      <View style={{flex:2, justifyContent:"center", alignItems:"center"}}>
      <Text style={{fontSize:24, color:"lightblue"}}>
              About our App
          </Text>
          <Text style={styles.header}>
              This App is created for students, workers and cooking beginer, who is out of family, having limited items to cook? We Can Help!
          </Text>
      </View>
      <View style={{flex:2}}>
      <Image
        style={styles.tinyLogo}
        source={{
          uri: 'https://reactnative.dev/img/tiny_logo.png',
        }}
      />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: '#fff',
    padding:5
  },
  header: {
    flex:1,
    alignItems:'center',
    fontSize:15,
  }
});
