import React, { Component, useState } from 'react';
import { StyleSheet, Text, View, Button, ImageBackground, TextInput } from 'react-native';
import ButtonRow from './ButtonRow'
// const App = () => {...}
export default function App({kind}) {
const [username,setUserName] = useState()
const [password, setPassword] = useState()
  return (
    <View style={styles.container}>
      <ImageBackground source={{uri: 'https://images.unsplash.com/photo-1481070555726-e2fe8357725c?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=870&q=80'}} style={styles.imageBackground}>
    </ImageBackground>
    <View style={styles.FormContainer}>
    <View>
      <Text style={styles.inputext}>MengCook</Text>
        <TextInput
          value={username}
          onChangeText={(username) => setUserName({ username })}
          placeholder='Email'
          style={styles.input}
        />
        <TextInput
          value={password}
          onChangeText={(password) => setPassword({ password })}
          placeholder='Password'
          secureTextEntry={true}
          style={styles.input}
        />
        <Text style={{alignSelf:"end", borderBottomWidth:1, marginBottom:10}}>Forgot Password?</Text>

        <Button
          title= {kind}
          style={styles.input}
          color="black"
        />
        <View style={{flexDirection:"row", marginTop:1, alignItems:"center", justifyContent:"center"}}>
        <Text style={{marginRight:3}}>Don't hava a account?</Text>
        <Text style={{borderBottomWidth:1}}>Sign Up now.</Text>
      </View>
      </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'stretch',
    justifyContent: 'center',
    alignItems:"center"
  },
  imageBackground: {
    width: '100%',
     height: '100%',
    opacity: 0.8
  },
  FormContainer: {
    flex: 1,
    width:500,
    height:550,
    alignItems: 'center',
    justifyContent: 'space-around',
    position: 'absolute', 
    backgroundColor:"white",
  },
  input: {
    width: 295,
    height: 45,
    padding: 10,
    borderWidth: 1,
    borderColor: 'black',
    marginBottom: 10,
  },
  inputext: {
    fontSize:40,
    padding: 10,
    textAlign:'center',
    fontWeight:'bold',
    marginBottom: 10,
  },
});
