import React, {useState, useEffect } from 'react';
import { StyleSheet, Text, View, Button, ImageBackground, TextInput, Image } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import { Icon } from 'react-native-elements'
import AsyncStorage from '@react-native-async-storage/async-storage';
// const { htmlToText } = require('html-to-text');

export default function App({route}) {
  // const recipeId = route.params.recipeId
  const recipeId = 716429
  const [title,setTitle] = useState()
  const [summary,setSummary] = useState()
  const [imageUrl,setImageUrl] = useState()
  const [ingredeints, setIngredients] = useState([])
  const [winePairing, setWinePairing] = useState([])
  const [wineText, setWineText] = useState()
  useEffect(() => {
    getData()
  }, []);

  const getData = async () => {
    fetch('https://api.spoonacular.com/recipes/'+recipeId+'/information?apiKey=79215483b19744afb1a46052ce726bdc')
      .then((response) => response.json())
      .then((cdata) => {
        console.log(cdata)
        setTitle(cdata.title)
        setSummary(cdata.summary)
        setImageUrl(cdata.image)
        setIngredients(cdata.extendedIngredients)
        console.log(cdata.winePairing.pairedWines)
        setWinePairing(cdata.winePairing.pairedWines)
        setWineText(cdata.winePairing.pairingText)
      })
    .catch((error)=> console.error(error))
  }

  const renderItem = ({item}) =>{
    return (
      <View style={{ padding:5}}>
      <Text style={{fontSize:20, fontWeight:600}}>{item.name}</Text>
      <Text>{item.amount} {item.unit}</Text>
      <Text>{item.consitency}</Text>
      </View>
    )
  }

  const wineItem = ({item}) =>{
    console.log(item)
    return (
      <View style={{ padding:5}}>
      <Text style={{fontSize:20, fontWeight:600}}>{item}</Text>
      </View>
    )
  }

  return (
    <View style={styles.container}>
      <View>
      <Text style={styles.header}>
         {title}
      </Text>
      </View>
      <View style={{marginBottom:20}}><Image source={{uri:imageUrl}} style={{width:700, height:400}}></Image></View>
      <View style={{borderBottomWidth:3, borderBottomColor: "rgb(226, 221, 204)", width:"85%", marginBottom:20}}>
        <Text style={{fontSize:26}}>Ingredients & Summary</Text>
      </View>
      <View style={{flexDirection:"row"}}>
        <View style={{flex:4, justifyContent:"center", alignItems:"center"}}>
        <FlatList data = {ingredeints} renderItem={renderItem} keyExtractor={item=>item.id}></FlatList>
        </View>
        <View style={{flex:4}}>
        <Text style={{fontSize:16}}>
        {summary}
        </Text>
        </View>
        <View style={{flex:1}}></View>
      </View>
      <View style={{borderBottomWidth:3, borderBottomColor: "rgb(226, 221, 204)", width:"85%", marginBottom:20}}>
        <Text style={{fontSize:26}}>WineParing</Text>
      </View>
      <View style={{flexDirection:"row"}}>
        {winePairing.length?<View style={{flex:4, justifyContent:"center", alignItems:"center"}}>
        <FlatList data = {winePairing} renderItem={wineItem} keyExtractor={item=>item}></FlatList>
        </View>:<View style={{flex:4, flex:4, justifyContent:"center", alignItems:"center"}}>N/A</View>}
        <View style={{flex:4}}>
        <Text style={{fontSize:16}}>
        {wineText}
        </Text>
        </View>
        <View style={{flex:1}}></View>
      </View>

      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    flex:1,
    alignItems:'center',
    fontSize:40,
    padding:25,
    fontWeight:"700",
    letterSpacing: 3
  },
  imageBackground: {
    width: '100%',
     height: '100%',
  },
  IngredientsInput:{
    height: 40,
    width:400,
    margin: 12,
    borderWidth: 1.5,
    padding: 10,
  },
});
