import React, { Component, useState } from 'react';
import { StyleSheet, Text, View, Button, ImageBackground, TextInput } from 'react-native';
import { FlatList } from 'react-native-gesture-handler';
import ButtonRow from './ButtonRow'

export default function App() {
  const [ingredient1, setIngredient1] = useState("")
  const [data,setData] = useState([])

  const createRecipe = () =>{
    fetch('https://api.spoonacular.com/recipes/findByIngredients?ingredients='+ingredient1+'&apiKey=79215483b19744afb1a46052ce726bdc')
      .then((response) => response.json())
      .then((cdata) => {
        console.log(cdata)
        setData(cdata)
      })
    .catch((error)=> console.error(error))
  }
  const renderItem = ({item}) =>{
    return (
      <View style={{flexDirection:"row"}}>
      <Text>{item.title}</Text>
      {/* <Image source={uri:{item.image}}></Image> */}
      </View>
    )
  }
  return (
    <View style={styles.container}>
      <ImageBackground source={{uri: 'https://images.unsplash.com/photo-1481070555726-e2fe8357725c?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=870&q=80'}} style={styles.imageBackground}>
    </ImageBackground>
      <View style={{alignItems:'center', position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, alignItems: 'center'}}>
        <View style={{flex:1}}>
          <Text style={styles.header}>
              Just fill in the ingredients that you have, then we can come up some good recipes for you!
          </Text>
        </View>
      <View style={{backgroundColor:"white", justifyContent:"center", alignItems:"center"}}>
        <TextInput style={styles.IngredientsInput} placeholder="Ingredients1" value={ingredient1} onChangeText={text=>{setIngredient1(text)}}></TextInput>
        {/* <TextInput style={styles.IngredientsInput}placeholder="Ingredients2"></TextInput>
        <TextInput style={styles.IngredientsInput}placeholder="Ingredients3"></TextInput>
        <TextInput style={styles.IngredientsInput}placeholder="Ingredients4"></TextInput>
        <TextInput style={styles.IngredientsInput}placeholder="Ingredients5"></TextInput> */}
        {/* <ButtonRow FirstButtonTitle = "create recipes" SecondButtonTitle = "more ingredients"/> */}
        <Button title="crete recipe" onPress={createRecipe}></Button>
      </View>
      <View><FlatList data = {data.slice(0,10)} renderItem={renderItem} keyExtractor={item=>item.title}></FlatList></View>
      </View>
      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'stretch',
    justifyContent: 'stretch',
  },
  header: {
    flex:1,
    alignItems:'center',
    fontSize:30,
    padding:25,
    fontWeight:"800",
  },
  imageBackground: {
    width: '100%',
     height: '100%',
    opacity: 0.8
  },
  IngredientsInput:{
    height: 40,
    width:400,
    margin: 12,
    borderWidth: 1.5,
    padding: 10,
  },
});
