import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import { StyleSheet, Text, View, Button, ImageBackground, StatusBar } from 'react-native';

import NewRecipeScreen from './NewRecipe'
import OldRecipeScreen from './OldRecipe'


const Stack = createNativeStackNavigator();

const MyStack = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator>

        <Stack.Screen
          name="Home"
          component={HomeScreen}
          //options={{ title: 'Welcome' }}
        />

        <Stack.Screen name="NewRecipe" component={NewRecipeScreen} />

        <Stack.Screen name="OldRecipe" component={OldRecipeScreen} />

      </Stack.Navigator>
    </NavigationContainer>
  );
};


const HomeScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
    <ImageBackground source={{uri: 'https://images.unsplash.com/photo-1481070555726-e2fe8357725c?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=870&q=80'}} style={styles.imageBackground}>
    </ImageBackground>
    <View style={{position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, alignItems: 'center', flexDirection:"row"}}>
    <View style={{flex:1}}></View>
    <View style={{flex:5}}>
    <View style={{flex:1,alignSelf:"center",marginBottom:10}}><Text style={styles.header}>Meng Cook</Text></View>
    <View style={{flex:1, marginBottom:10}}><Text style={styles.introduction}>Out of town, away from family, having limited items to cook? We can help you to make yummy dishes.

</Text></View>
    <View style={styles.buttonRow}>
      <View style={{marginRight:5}}><Button title ="New Recipes" onPress={() => navigation.navigate("NewRecipe")}>
      </Button></View>
      <View><Button title ="Old Recipes" color="red" onPress={() => navigation.navigate("OldRecipe")}>
      </Button></View>
    </View>
    </View>
    <View style={{flex:1}}></View>
    </View>
    <StatusBar style="auto" />
  </View>
  );
};

// ProfileScreen function is called with a JSON object
//  {navigation:..., route:...,  otherstuff}
// const NewRecipe = ({ navigation, route }) => {
//   return <Text>this is new recipe</Text>;
//        // we're using the parameter name passed in from the HomeScreen
// };

// const OldRecipe = ({ navigation, route }) => {
//   return <Text>this is recipe</Text>;
//        // we're using the parameter name passed in from the HomeScreen
// };

export default MyStack;


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent:"center",
    flexDirection:"row"
  },
  header: {
    fontSize: 48,
    fontWeight: "bold",
    flex:2
  },
  introduction: {
    fontSize:16,
    color:"white",
    fontWeight:"bold"
  },
  imageBackground: {
    width: '100%',
     height: '100%',
    opacity: 0.8
  },
  buttonRow: {
    flex:1,
    flexDirection:"row",
    alignSelf:"center"
  }
});
