import React, { Component, useState } from 'react';
import { StyleSheet, Text, View, Button, TextInput } from 'react-native';
import ButtonRow from './ButtonRow'
// const App = () => {...}
export default function App({kind, forgotPasswordShow, bottomMessage1, bottomMessage2, confirmPasswordShow}) {
const [username,setUserName] = useState()
const [password, setPassword] = useState()
const [confirmPassword, setConfirmPassword] = useState()

const check = () => {
    if (kind == "Login") {
        if (checkUserInfo()) {
            alert("Correct User Information")
        }
        else{
            alert("Wrong User Information")
        }
    }
    else{
        if (checkPassword()) {
            alert("Have same password")
        }
        else{
            alert("password and confirm passwrord ar not the same")
        }
    }
}
const checkPassword = () => {
    if(password == confirmPassword){
        return True
    }
    return False
}
  return (
    <View style={styles.FormContainer}>
    <View>
      <Text style={styles.inputext}>MengCook</Text>
        <TextInput
          value={username}
          onChangeText={(username) => setUserName({ username })}
          placeholder='Email'
          style={styles.input}
        />
        <TextInput
          value={password}
          onChangeText={(password) => setPassword({ password })}
          placeholder='Password'
          secureTextEntry={true}
          style={styles.input}
        />
        {confirmPasswordShow?<TextInput
          value={confirmPassword}
          onChangeText={(confirmPassword) => setConfirmPassword({ confirmPassword })}
          placeholder='Confirm Password'
          secureTextEntry={true}
          style={styles.input}
        />:null}
        {forgotPasswordShow?<Text style={{alignSelf:"end", borderBottomWidth:1, marginBottom:10}}>Forgot Password?</Text>:null}
        <Button
          title= {kind}
          style={styles.input}
          color="black"
          onPress = {check}
        />
        <View style={{flexDirection:"row", marginTop:1, alignItems:"center", justifyContent:"center"}}>
        <Text style={{marginRight:3}}>{bottomMessage1}</Text>
        <Text style={{borderBottomWidth:1}}>{bottomMessage2}</Text>
      </View>
      </View>
      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'stretch',
    justifyContent: 'center',
    alignItems:"center"
  },
  imageBackground: {
    width: '100%',
     height: '100%',
    opacity: 0.8
  },
  FormContainer: {
    flex: 1,
    width:500,
    height:550,
    alignItems: 'center',
    justifyContent: 'space-around',
    position: 'absolute', 
    backgroundColor:"white",
  },
  input: {
    width: 295,
    height: 45,
    padding: 10,
    borderWidth: 1,
    borderColor: 'black',
    marginBottom: 10,
  },
  inputext: {
    fontSize:45,
    padding: 10,
    textAlign:'center',
    fontWeight:'bold',
    marginBottom: 30,
  },
});
