import React, { Component } from 'react';
import { StyleSheet, Text, View, Button, ImageBackground, TextInput } from 'react-native';
import ButtonRow from './ButtonRow'
// const App = () => {...}
export default function App() {
  return (
    <View style={styles.container}>
      <View style={{flex:2}}>
          <Text style={styles.header}>
              This is App is created for students, workers and cooking beginer, who is out of family, having limited items to cook? We Can Help!
          </Text>
      </View>
      <View style={{flex:2}}>
          <Text style={styles.header}>
              Just fill in the ingredients that you have, then we can come up some good recipes for you!
          </Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center'
  },
  header: {
    flex:1,
    alignItems:'center',
    fontSize:20,
    padding:25,
    color:"while",
  },
  imageBackground: {
    width: '100%',
     height: '100%',
    opacity: 0.8
  },
  IngredientsInput:{
    height: 40,
    width:400,
    margin: 12,
    borderWidth: 1.5,
    padding: 10,
  },
});
