import React, { Component } from 'react';
import { StyleSheet, Text, View, Button, Image, TextInput } from 'react-native';
import ButtonRow from './ButtonRow'
// const App = () => {...}
export default function App() {
  return (
    <View style={styles.container}>
      <View style={{flex:2, justifyContent:"center", alignItems:"center",alignSelf:"center"}}>
      <Text style={{fontSize:24, color:"lightblue"}}>
              About our App
          </Text>
          <Text style={styles.header}>
              This App is created for students, workers and cooking beginer, who is out of family, having limited items to cook? We Can Help!
          </Text>
      </View>
      <View style={{flex:2}}>
          <Text style={{fontSize:24, color:"lightblue"}}>What We Have?</Text>
          <Text style={{fontSize:24, color:"lightblue"}}>Provide New Recipes</Text>
          <Image style={styles.images} source = {{uri:'https://images.unsplash.com/photo-1576867757603-05b134ebc379?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1770&q=80'}}></Image>
          <Text style={{fontSize:24, color:"lightblue"}}>Saved Your Own Recipes</Text>
          <Image></Image>
          <Text style={{fontSize:24, color:"lightblue"}}>Share Your Recipes</Text>
          <Image></Image>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: '#fff',
    padding:5
  },
  header: {
    flex:1,
    alignItems:'center',
    fontSize:15,
  },
  images: {
      width:"100%",
      height:"auto"
  }
});
